import React, { useEffect, useState } from "react";
import { useTheme } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { timeSince } from "../utils/utils.js";

const getRSSFeed = async () => {
  const response = await fetch("/api/rss_feed");
  const body = await response.json();
  console.log({ body });
  if (response.status !== 200) {
    throw Error(body.message);
  }
  return body;
};

const RSSFeed = ({ layout }) => {
  console.log({ layout });
  const [RSS, setRSS] = useState([]);

  useEffect(async () => {
    const response = await getRSSFeed();
    console.log({ response });
    setRSS(response);
  }, []);

  return (
    <div
      style={{
        width: "80%",
        margin: "10px auto",
        padding: "10px",
        textAlign: "left",
      }}
    >
      {layout == "default"
        ? RSS.sort(function (a, b) {
            // Turn your strings into dates, and then subtract them
            // to get a value that is either negative, positive, or zero.
            return new Date(b.date) - new Date(a.date);
          }).map((article) => (
            <Card style={{ border: "solid rgb(239, 243, 244)", margin: "1%" }}>
              <CardContent>
                <Typography variant="h5" component="div">
                  {article.title}
                </Typography>
                <Typography variant="p">
                  {timeSince(new Date(article.pubDate))}
                </Typography>
                <Typography variant="body2">
                  <span>
                    <span
                      dangerouslySetInnerHTML={{
                        __html: article.content.slice(0, 400),
                      }}
                    />
                    <b>...</b>
                  </span>
                </Typography>
              </CardContent>
              <CardActions>
                <Button target="_blank" href={article.link} size="small">
                  Learn More
                </Button>
              </CardActions>
            </Card>
          ))
        : RSS.map((article) => (
            <div
              style={{
                margin: "3%",
                textAlign: "left",
                borderBottom: "solid .5 #828282",
              }}
            >
              <a href={article.link}>
                <tr>{article.title}</tr>
                <tr>{article.link.split("/")[2]}</tr>
              </a>
            </div>
          ))}
    </div>
  );
};

export default RSSFeed;
