import logo from "./logo.svg";
import "./App.css";
import ResponsiveAppBar from "./components/ResponsiveAppBar.js";
import TwitterFeed from "./components/TwitterFeed.js";
import RSSFeed from "./components/RSSFeed.js";
import ShowStories from "./components/ShowStories.js";
import Button from "@mui/material/Button";
import React, { useState, useEffect } from "react";

function App() {
  const [layout, setLayout] = useState(localStorage.getItem("layout"));

  const setLayoutPermanent = (layout) => {
    // setter
    localStorage.setItem("layout", layout);
    setLayout(layout);
  };
  return (
    <div className="App">
      <ResponsiveAppBar />
      <Button onClick={() => setLayoutPermanent(undefined)} variant="outlined">
        Default
      </Button>
      <Button onClick={() => setLayoutPermanent("minimal")} variant="outlined">
        Minimal
      </Button>
      <div className="Content">
        {layout == "undefined" ? (
          <>
            <div className="Left">
              <h3>Daily HN Content</h3>
              <div className="HNContainer">
                <ShowStories />
              </div>
            </div>
            <div className="Right">
              <h3>Recent Tweets</h3>
              <div className="TwitterContainer">
                <TwitterFeed />
              </div>
              <h3>Your News</h3>
              <div className="NewsContainer">
                <RSSFeed />
              </div>
            </div>
          </>
        ) : (
          <div style={{ display: "flex", flexDirection: "row" }}>
            <div>
              <ShowStories />
            </div>
            <div>
              <TwitterFeed />
            </div>
            <div>
              <RSSFeed />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
