import React, { useEffect, useState } from "react";
import { useTheme } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import SkipPreviousIcon from "@mui/icons-material/SkipPrevious";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import SkipNextIcon from "@mui/icons-material/SkipNext";

const getUserTweets = async () => {
  const response = await fetch("/twitter_id");
  const body = await response.json();

  if (response.status !== 200) {
    throw Error(body.message);
  }
  return body;
};

const getMediaUrl = (tweetMedia, tweet) => {
  console.log({ tweetMedia });

  tweetMedia
    .filter(
      tweetMedia.filter((media) => media.key === tweet.attachments.mediaKeys[0])
    )
    .map((obj) => obj.preview_image_url);
};

const TwitterFeed = () => {
  const [tweets, setTweets] = useState([]);
  const [tweetMedia, setTweetMedia] = useState();
  const theme = useTheme();

  console.log(tweets);
  useEffect(async () => {
    console.log(tweetResponse.includes);
    const tweetResponse = await getUserTweets();
    setTweets(tweetResponse.data);
    setTweetMedia(tweetResponse.includes);
  }, []);

  return (
    <>
      {tweets &&
        tweets.map((tweet) => (
          <Card sx={{ display: "flex" }}>
            <Box sx={{ display: "flex", flexDirection: "column" }}>
              <CardContent sx={{ flex: "1 0 auto" }}>
                <CardMedia
                  component="img"
                  sx={{ width: 151 }}
                  image={getMediaUrl(tweetMedia, tweet)}
                  alt="Live from space album cover"
                />

                <Typography component="div" variant="p">
                  {tweet.text}
                </Typography>
                <Typography
                  variant="subtitle1"
                  color="text.secondary"
                  component="div"
                >
                  {tweet.id}
                </Typography>
              </CardContent>
            </Box>
          </Card>
        ))}
    </>
  );
};

export default TwitterFeed;
